mapper.select:
SELECT id
FROM tb_checkin
WHERE user_id = #{userId}
  AND date = CURRENT_DATE
  AND create_time BETWEEN #{start} AND #{end}
LIMIT 1

SELECT COUNT(*) AS count
FROM tb_checkin
WHERE user_id = #{userId}

SELECT CAST(date AS CHAR)         AS date,
       IF(status = 1, "正常", "迟到") AS status
FROM tb_checkin
WHERE user_id = #{userId}
  AND date BETWEEN #{startDate}
    AND #{endDate}
